mapper.select:
SELECT o.*,c.real_name FROM jcart_order as o
left join jcart_customer as c
on o.customer_id = c.customer_id


    order_id = #{orderId}


    AND o.create_time >= #{startTime}


    AND o.create_time <= #{endTime}


    AND o.status = #{status}


    AND total_price = #{totalPrice}


    AND real_name like concat('%',#{customerName},'%')


order by create_time desc
